"use client";

import { useState } from "react";
import Image from "next/image";
import { GripVertical } from "lucide-react";
import { Headline } from "@/components/shared/headline";

function Feature() {
  const [inset, setInset] = useState<number>(50);
  const [onMouseDown, setOnMouseDown] = useState<boolean>(false);

  const onMouseMove = (e: React.MouseEvent | React.TouchEvent) => {
    if (!onMouseDown) return;

    const rect = e.currentTarget.getBoundingClientRect();
    let x = 0;

    if ("touches" in e && e.touches.length > 0) {
      x = e.touches[0].clientX - rect.left;
    } else if ("clientX" in e) {
      x = e.clientX - rect.left;
    }

    const percentage = (x / rect.width) * 100;
    setInset(percentage);
  };

  return (
    <div className="w-full">
      <div className="mx-auto">
        <div className="flex flex-col gap-4">
          <Headline
            title="Comparez les devis"
            description="Comparez les devis générés par l’extension avec les devis manuels."
            badge={{ text: "Platform", isBadge: false }}
          />
          <div className="pt-12 w-full">
            <div
              className="relative aspect-video w-full h-full overflow-hidden rounded-2xl select-none"
              onMouseMove={onMouseMove}
              onMouseUp={() => setOnMouseDown(false)}
              onTouchMove={onMouseMove}
              onTouchEnd={() => setOnMouseDown(false)}
            >
              <div
                className="bg-muted h-full w-1 absolute z-20 top-0 -ml-1 select-none"
                style={{
                  left: inset + "%",
                }}
              >
                <button
                  className="bg-muted rounded hover:scale-110 transition-all w-5 h-10 select-none -translate-y-1/2 absolute top-1/2 -ml-2 z-30 cursor-ew-resize flex justify-center items-center"
                  onTouchStart={(e) => {
                    setOnMouseDown(true);
                    onMouseMove(e);
                  }}
                  onMouseDown={(e) => {
                    setOnMouseDown(true);
                    onMouseMove(e);
                  }}
                  onTouchEnd={() => setOnMouseDown(false)}
                  onMouseUp={() => setOnMouseDown(false)}
                >
                  <GripVertical className="h-4 w-4 select-none" />
                </button>
              </div>
              <Image
                src="https://images.unsplash.com/photo-1744359678374-4769eacf44d6?q=80&w=1364&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D"
                alt="feature8"
                width={1920}
                height={1080}
                priority
                className="absolute left-0 top-0 z-10 w-full h-full aspect-video rounded-2xl select-none border"
                style={{
                  clipPath: "inset(0 0 0 " + inset + "%)",
                }}
              />
              <Image
                src="https://images.unsplash.com/photo-1753808645289-283497260e33?q=80&w=2375&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D"
                alt="darkmode-feature8.png"
                width={1920}
                height={1080}
                priority
                className="absolute left-0 top-0 w-full h-full aspect-video rounded-2xl select-none border"
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export { Feature };

import { Badge } from "@/components/ui/badge";
import { ReactNode } from "react";

interface HeadlineProps {
  title: ReactNode;
  description: string;
  badge: {
    text: string;
    isBadge: boolean;
  };
}

export function Headline({ title, description, badge }: HeadlineProps) {
  const displayBadge = () => {
    if (badge.isBadge) {
      return (
        <Badge variant="outline" className={`text-primary border-primary`}>
          {badge.text}
        </Badge>
      );
    }
    return <p className={`text-sm lg:text-lg text-primary font-semibold uppercase`}>{badge.text}</p>;
  };
  return (
    <div className="flex flex-col items-center gap-4 text-center mb-0 max-w-5xl mx-auto">
      {displayBadge()}
      <h2 className="text-4xl md:text-6xl font-semibold text-foreground leading-tight">{title}</h2>
      <p className="text-xl md:text-2xl text-muted-foreground max-w-4xl">{description}</p>
    </div>
  );
}
